import React from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';

const LocationSpecificPets = () => {
  const location = useLocation();
  const state = location.state;

  if (!state || !state.data) {
    return <div>Loading ... </div>; 
  }

  const { data } = state;
  // console.log(data, "data in specific pets");

  return (
    <div>
      <h2>Search Results for: {data.searchText}</h2>
      <h3>Pet Type: {data.petType}</h3>
      <div className="pet-card-container">
        {data.animals.length === 0 ? (
          <p>No animals found.</p> // Display an error message
        ) : (
          data.animals.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))
        )};
      </div>
    </div>
  );
};

export default LocationSpecificPets;
