import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import PetCard from './PetCard';
import '../styles/LocationSpecificPets.css';

const LocationSpecificPets = ({
  favoritePets,
  addToFavorites,
  removeFromFavorites
}) => {
  const location = useLocation();
  const state = location.state;

  const [currentPage, setCurrentPage] = useState(1); // Move useState to the top level

  useEffect(() => {
    if (!state || !state.data) {
      return; // Early return if there's no data
    }

    const { data, petType, searchText } = state; // Define these variables here
    const itemsPerPage = 10;

    // Calculate the index range for the current page
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    // Slice the data to display only the items for the current page
    const petsToDisplay = data.animals.slice(startIndex, endIndex);

    // Rest of your component code...
  }, [state, currentPage]);

  return (
    <div className="location-specific-pets">
      <h2 className="search-results-title">Search Results for:</h2>
      <h3 className="pet-type-title">
        All {petType}s in the {searchText} area
      </h3>
      <div className="pet-card-container">
        {petsToDisplay.length === 0 ? (
          <p className="no-animals-message">No animals found.</p>
        ) : (
          petsToDisplay.map((pet) => (
            <PetCard
              key={pet.id}
              pet={pet}
              addToFavorites={addToFavorites}
              removeFromFavorites={removeFromFavorites}
              isFavorite={favoritePets.some(
                (favoritePet) => favoritePet.id === pet.id
              )}
            />
          ))
        )}
      </div>
      <div className="pagination">
        <button
          onClick={() => setCurrentPage(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous Page
        </button>
        <span>Page {currentPage}</span>
        <button
          onClick={() => setCurrentPage(currentPage + 1)}
          disabled={endIndex >= data.animals.length}
        >
          Next Page
        </button>
      </div>
    </div>
  );
};

export default LocationSpecificPets;
