import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function AllPetsPage() {
  const { category } = useParams();
  const [animalCache, setAnimalCache] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const petsPerPage = 20;

  const fetchAnimalsByType = async (type, page) => {
    try {
      const response = await axios.get(
        `http://localhost:3002/api/petfinder?type=${type}&offset=${(page - 1) * petsPerPage}&limit=${petsPerPage}`
      );
      const data = response.data;

      console.log('API Response:', data);

      if (data && data.animals) {
        setAnimalCache((prevCache) => [...prevCache, ...data.animals]);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnimalsByType(category, currentPage);
  }, [category, currentPage]);

  const totalPets = animalCache.length;

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const start = (currentPage - 1) * petsPerPage;
  const end = start + petsPerPage;
  const displayedPets = animalCache.slice(start, end);

  const rawData = displayedPets.map((pet, index) => (
    <pre key={index}>{JSON.stringify(pet, null, 2)}</pre>
  ));

  return (
    <div>
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          {rawData}
          <div>
            <button onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1}>
              Previous Page
            </button>
            <button onClick={() => paginate(currentPage + 1)} disabled={currentPage * petsPerPage >= totalPets}>
              Next Page
            </button>
          </div>
          <p>{`Pets displayed: ${displayedPets.length}`}</p> {/* This line shows the number of pets displayed */}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
