import React from 'react';
import { useLocation } from 'react-router-dom';
import { useSearchResults } from './SearchResultContext';
import PetCard from './PetCard'; // Import the PetCard component

const LocationSpecificPets = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const searchText = searchParams.get('searchText');
  const petType = searchParams.get('petType');
  const { searchResults } = useSearchResults();

  if (!searchResults) {
    // Data hasn't been loaded yet, show message
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h2>Search Results for: {searchText}</h2>
      <h3>Pet Type: {petType}</h3>
      <div className="pet-card-container">
        {searchResults.animals.map((pet) => (
          <PetCard
            key={pet.id}
            pet={pet}
            // Pass any other necessary props to the PetCard component
          />
        ))}
      </div>
    </div>
  );
};

export default LocationSpecificPets;
