import React from 'react';
import './styles/Favorites.css';


const Favorites = () => {
  const favoritedPets = [
    {
      id: 1,
      name: 'Buddy',
      photo: '/images/dog1.jpg',
      shelter: 'Happy Paws Shelter',
    },
    {
      id: 2,
      name: 'Daisy',
      photo: '/images/dog2.jpg',
      shelter: 'Loving Hearts Rescue',
    },
    // Add more favorited pets as needed
  ];

  return (
    <div className="favorites-container">
      <h2>Favorites</h2>
      <div className="favorite-cards">
        {favoritedPets.map(pet => (
          <div className="favorite-card" key={pet.id}>
            <img src={pet.photo} alt={pet.name} />
            <h3>{pet.name}</h3>
            <p>{pet.shelter}</p>
            <button className="favorite-heart">&#10084;</button>
            <button className="more-info-button">More Info</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Favorites;
