import React from 'react';
import { useLocation } from 'react-router-dom';
import { useSearchResults } from './SearchResultContext';

const LocationSpecificPets = () => {
  const location = useLocation();
  const searchParams = new URLSearchParams(location.search);
  const searchText = searchParams.get('searchText');
  const petType = searchParams.get('petType');
  const { searchResults } = useSearchResults();

  if (!searchResults) {
    // Data hasn't been loaded yet, you can show a loading indicator or message here
    return <div>Loading...</div>;
  }

  // Use searchText and petType to fetch and display search results

  return (
    <div>
      <h2>Search Results for: {searchText}</h2>
      <h3>Pet Type: {petType}</h3>
      {/* Display the search results here */}
    </div>
  );
};

export default LocationSpecificPets;
