import React, { useState } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ filters, onFilterChange, searchResults, handleSearch }) {
  const [selectedFilters, setSelectedFilters] = useState({
    size: 'medium', // Default filter values
    breed: 'breed1', // Default breed filter
    age: 'puppy',   // Default age filter
    gender: 'male', // Default gender filter
    shelter: 'shelter1', // Default shelter filter
  });

  const handleFilterChange = (filterName, value) => {
    setSelectedFilters({
      ...selectedFilters,
      [filterName]: value,
    });
  };

  const handleFilterSubmit = () => {
    // Handle the submission of selectedFilters here
    // You can call a function like handleSearch(selectedFilters) to trigger the search.
    handleSearch(selectedFilters);
  };

  return (
    <div className="main-page">
      <div className="search-bar">
        <SearchBar onSearch={handleSearch} />
      </div>
      <div className="sidebar">
        <div className="filters">
          <Filter filters={selectedFilters} onFilterChange={handleFilterChange} />
          <button onClick={handleFilterSubmit}>Apply Filters</button>
        </div>
      </div>
      <div className="content">
      {searchResults ? (
        <div className="pet-card-list">
          {searchResults.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
    </div>
  );
}

export default MainPage;
