import React, { useState, useEffect } from 'react';
import '../styles/MainPage.css';
import SearchBar from './SearchBar';
import PetCard from './PetCard';
import Filter from './Filter';
import SampleData from './SampleData';
import FavoritesPage from './FavoritesPage';

function MainPage({ onFilterChange, favoritePets, setFavoritePets }) {
  const initialFilters = {
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
    tags: 'any',
    name: 'any',
    description: 'any',
    photos: 'any',
    status: 'any',
    contact: 'any',
  };

  const [filters, setFilters] = useState(initialFilters);
  const [searchResults, setSearchResults] = useState([]);

  // Functions for Managing Favorites
  const addToFavorites = (pet) => {
    // Check if the pet is already in favorites to avoid duplicates
    if (!favoritePets.some((favoritePet) => favoritePet.id === pet.id)) {
      setFavoritePets([...favoritePets, pet]);
    }
  };

  const removeFromFavorites = (petId) => {
    const updatedFavorites = favoritePets.filter((pet) => pet.id !== petId);
    setFavoritePets(updatedFavorites);
  };


  useEffect(() => {
    // When the component mounts or filters change, filter the data
    filterPets(filters);
  }, [filters]);

  const filterPets = (filters) => {
    let filteredData = [...SampleData];

    // Filter by pet type
    if (filters.type !== 'any') {
      filteredData = filteredData.filter((pet) => pet.type.toLowerCase() === filters.type.toLowerCase());
    }
    // Filter by breed
    if (filters.breed !== 'any') {
      filteredData = filteredData.filter((pet) => pet.breed.toLowerCase() === filters.breed.toLowerCase());
    }

    // Filter by age
    if (filters.age !== 'any') {
      filteredData = filteredData.filter((pet) => pet.age.toLowerCase() === filters.age);
    }

    // Filter by gender
    if (filters.gender !== 'any') {
      filteredData = filteredData.filter((pet) => pet.gender.toLowerCase() === filters.gender);
    }

    // Filter by size
    if (filters.size !== 'any') {
      filteredData = filteredData.filter((pet) => pet.size.toLowerCase() === filters.size);
    }

    // Filter by coat
    if (filters.coat !== 'any') {
      filteredData = filteredData.filter((pet) => pet.coat.toLowerCase() === filters.coat);
    }

    setSearchResults(filteredData);
  };

  const handleFilterChange = (filterName, value) => {
    const updatedFilters = { ...filters, [filterName]: value };
    setFilters(updatedFilters);
  };

  return (
    <div className="main-page">
      <div className="sidebar">
        <div className="filters">
          <Filter filters={filters} onFilterChange={handleFilterChange} />
        </div>
      </div>
      <div className="content">
        <div className="pet-card-list">
        {searchResults.map((pet) => (
          <PetCard
            key={pet.id}
            pet={pet}
            addToFavorites={addToFavorites}
            removeFromFavorites={removeFromFavorites}
            isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
          />
          ))}

        </div>
      </div>
    </div>
  );
}

export default MainPage;
