import React, { useState, useEffect, useRef } from 'react';
import '../styles/MainPage.css';
import PetCard from './PetCard';
import Filter from './Filter';

function MainPage({ favoritePets, addToFavorites, removeFromFavorites }) {
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const currentPageRef = useRef(1);
  const itemsPerPage = 20;
  const minimumPets = 400;
  const [cachedData, setCachedData] = useState([]);
  const [totalPages, setTotalPages] = useState(0);

  const [selectedFilters, setSelectedFilters] = useState({
    type: 'any',
    breed: 'any',
    age: 'any',
    gender: 'any',
    size: 'any',
    coat: 'any',
  });

  useEffect(() => {
    // Fetch data from the API when the component first loads
    const fetchData = async () => {
      try {
        const endpoint = 'http://localhost:3002/api/petfinder?perPage=400'; // Fetch all pets in a single request
        const response = await fetch(endpoint);
        const data = await response.json();

        console.log('API Data:', data);

        if (data && data.animals && data.animals.length > 0) {
          setSearchResults(data.animals);
          setLoading(false);
        }
      } catch (error) {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      }
    };

    // Check if cached data is empty and fetch data if needed
    if (searchResults.length === 0) {
      fetchData();
    } else {
      setLoading(false);
    }
  }, [searchResults]);

  // Define the handlePageChange function to handle page changes
  const handlePageChange = (page) => {
    currentPageRef.current = page;
    const startIndex = (page - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const petsToDisplay = searchResults.slice(startIndex, endIndex);
    setSearchResults(petsToDisplay);
  };

  useEffect(() => {
    // Calculate the total number of pages based on the fetched data and items per page
    const totalPages = Math.ceil(searchResults.length / itemsPerPage);

    // Update the current page reference if it's out of bounds
    if (currentPageRef.current < 1) {
      currentPageRef.current = 1;
    } else if (currentPageRef.current > totalPages) {
      currentPageRef.current = totalPages;
    }
  }, [searchResults]);



  // Function to handle filter changes
  const handleFilterChange = (newFilters) => {
    setSelectedFilters(newFilters);
    currentPageRef.current = 1;
  };

  const renderPetCards = () => {
    if (!loading) {
      if (searchResults.length > 0) {
        return searchResults.map((pet) => (
          <PetCard
            key={pet.id}
            pet={pet}
            addToFavorites={addToFavorites}
            removeFromFavorites={removeFromFavorites}
            isFavorite={favoritePets.some((favoritePet) => favoritePet.id === pet.id)}
          />
        ));
      } else {
        return <p>No pets match your criteria.</p>;
      }
    } else {
      return <p>Loading...</p>;
    }
  };




  return (
    <div className="main-page">
      {/* ... (Rest of your component code) */}

      <div className="content">
        {/* Render pet cards based on filtered search results */}
        <div className="pet-card-list">{renderPetCards()}</div>
        <div className="pagination-horizontal">
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index}
              onClick={() => handlePageChange(index + 1)}
              className={currentPageRef.current === index + 1 ? 'active' : ''}
            >
              {index + 1}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
}

export default MainPage;
