import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';

function shuffleArray(array) {
  for (let i = array.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [array[i], array[j]] = [array[j], array[i]];
  }
}

function AllPetsPage() {
  const { category } = useParams();
  const [loading, setLoading] = useState(true);
  const [cache, setCache] = useState([]);
  const petsPerPage = 20;
  const otherAnimalTypes = ["horse", "bird", "barnyard"];

  const fetchAnimalsByCategory = async (category) => {
    const animals = [];

    if (category === "dog" || category === "cat") {
      const endpoint = `http://localhost:3002/api/petfinder?type=${category}&limit=${100}`;
      const response = await fetch(endpoint);
      const data = await response.json();

      if (data && data.animals) {
        animals.push(...data.animals.filter((animal) => animal.photos.length > 0));
      }
    }

    if (category === "other") {
      for (const animalType of otherAnimalTypes) {
        const endpoint = `http://localhost:3002/api/petfinder?type=${animalType}&limit=${petsPerPage}`;
        const response = await fetch(endpoint);
        const data = await response.json();

        if (data && data.animals) {
          animals.push(...data.animals.filter((animal) => animal.photos.length > 0));
        }
      }
    }

    // Shuffle the animals array
    shuffleArray(animals);

    return animals.slice(0, petsPerPage);
  };

  useEffect(() => {
    async function fetchData() {
      const animals = await fetchAnimalsByCategory(category);
      setCache(animals);
      setLoading(false);
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }

    fetchData();
  }, [category]);

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="pet-list">
          {cache.map((pet) => (
            <PetCard key={pet.id} pet={pet} />
          ))}
        </div>
      )}
    </div>
  );
}

export default AllPetsPage;
