import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';
import { ClientSession } from 'aiohttp';

async function fetchAnimalsByType(type, page) {
  const url = `http://localhost:3002/api/petfinder?type=${type}&offset=${(page - 1) * 20}&limit=20`;
  const response = await fetch(url);
  const data = await response.json();
  return data;
}

function AllPetsPage() {
  const { category } = useParams();
  const [allPets, setAllPets] = useState([]);
  const [displayedPets, setDisplayedPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1);
  const petsPerPage = 20;

  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      try {
        const data = await fetchAnimalsByType(category, currentPage);
        if (data && data.animals) {
          setAllPets(data.animals);
          setLoading(false);
        }
      } catch (error) {
        console.error('Error fetching data:', error.message);
        setLoading(false);
      }
    }

    fetchData();
  }, [category, currentPage]);

  useEffect(() => {
    const start = (currentPage - 1) * petsPerPage;
    const end = start + petsPerPage;
    setDisplayedPets(allPets.slice(start, end));
  }, [allPets, currentPage]);

  const totalPets = allPets.length;

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  const totalPages = Math.ceil(totalPets / petsPerPage);
  const paginationNumbers = Array.from({ length: Math.max(9, totalPages) }, (_, index) => (
    <button key={index + 1} onClick={() => paginate(index + 1)}>
      {index + 1}
    </button>
  ));

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div className="pet-list">
            {displayedPets.map((pet) => (
              <PetCard key={pet.id} pet={pet} />
            ))}
          </div>
          <div className="pagination">
            {paginationNumbers}
          </div>
        </>
      )}
    </div>
  );
}

export default AllPetsPage;
