import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/AllPetsPage.css';
import PetCard from './PetCard';

function AllPetsPage() {
  const { category } = useParams();
  const [allPets, setAllPets] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState(1); // Start at page 1
  const petsPerPage = 20;

  const fetchAnimalsByType = async (type, page) => {
    try {
      const response = await fetch(
        `http://localhost:3002/api/petfinder?type=${type}&offset=${(page - 1) * petsPerPage}&limit=${petsPerPage}`
      );
      const data = await response.json();

      console.log('API Response:', data);

      if (data && data.animals) {
        setAllPets(data.animals);
        setLoading(false);
      }
    } catch (error) {
      console.error('Error fetching data:', error.message);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAnimalsByType(category, currentPage);
  }, [category, currentPage]);

  const totalPets = allPets.length;

  const paginate = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Calculate the total number of pages
  const totalPages = Math.ceil(totalPets / petsPerPage);

  // Show at least 1-9 pagination numbers
  const paginationNumbers = Array.from({ length: Math.max(9, totalPages) }, (_, index) => (
    <button key={index + 1} onClick={() => paginate(index + 1)}>
      {index + 1}
    </button>
  ));

  return (
    <div className="all-pets-page">
      <h2>{`All ${category.charAt(0).toUpperCase() + category.slice(1)}s`}</h2>
      {loading ? (
        <p>Loading...</p>
      ) : (
        <>
          <div className="pet-list">
            {allPets.map((pet) => (
              <PetCard key={pet.id} pet={pet} />
            ))}
          </div>
          <div className="pagination">
            {paginationNumbers}
          </div>
        </>
      )}
    </div>
  );
}

export default AllPetsPage;
